import React from "react";
import { Card, Button, OverlayTrigger, Popover } from "react-bootstrap";

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  onActionClick,
  showCertificate,
  onDelete
}) => {
  const renderPopover = (
    <Popover id={`popover-${title}`} style={{ maxWidth: "300px" }}>
      <Popover.Body style={{ padding: "12px", fontSize: "0.9rem" }}>
        {description}
      </Popover.Body>
    </Popover>
  );

  return (
    <OverlayTrigger
      placement="top"
      delay={{ show: 150, hide: 300 }}
      overlay={renderPopover}
      trigger={["hover", "focus"]}
    >
      <Card style={{ 
        height: "100%",
        border: "none",
        boxShadow: "0 0.125rem 0.25rem rgba(0, 0, 0, 0.075)",
        transition: "transform 0.2s ease-in-out",
        ':hover': {
          transform: "translateY(-2px)",
          boxShadow: "0 0.5rem 1rem rgba(0, 0, 0, 0.15)"
        }
      }}>
        <Card.Img
          variant="top"
          src={`/Img/${image}`}
          alt={title}
          style={{ 
            height: "160px",
            objectFit: "cover",
            borderTopLeftRadius: "calc(0.25rem - 1px)",
            borderTopRightRadius: "calc(0.25rem - 1px)"
          }}
        />
        
        <Card.Body style={{ 
          display: "flex",
          flexDirection: "column",
          padding: "1.25rem"
        }}>
          <Card.Title style={{ 
            fontSize: "1.1rem",
            marginBottom: "0.75rem",
            fontWeight: "500"
          }}>
            {title}
          </Card.Title>
          
          <div style={{ 
            width: "100%",
            height: "6px",
            backgroundColor: "#e9ecef",
            borderRadius: "3px",
            marginBottom: "0.5rem"
          }}>
            <div 
              style={{ 
                width: `${completed}%`,
                height: "100%",
                backgroundColor: "#6f42c1",
                borderRadius: "3px",
                transition: "width 0.5s ease"
              }} 
            />
          </div>
          
          <span style={{ 
            fontSize: "0.85rem",
            color: "#6c757d",
            marginBottom: "1rem"
          }}>
            {completed}% completed
          </span>
          
          <div style={{ marginTop: "auto" }}>
            <Button
              variant={showCertificate ? "outline-primary" : "primary"}
              style={{ width: "100%" }}
              href={onActionClick ? undefined : actionLink}
              onClick={onActionClick}
              disabled={completed < 100 && showCertificate}
            >
              {actionText}
            </Button>
            
            {showCertificate && (
              <Button
                variant="outline-danger"
                style={{ 
                  width: "100%",
                  marginTop: "0.5rem"
                }}
                onClick={onDelete}
              >
                Delete
              </Button>
            )}
          </div>
        </Card.Body>
      </Card>
    </OverlayTrigger>
  );
};

export default CourseCard;
