import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import { PhotoProvider, PhotoView } from "react-photo-view";
import "react-photo-view/dist/react-photo-view.css";
import "../styles/Coursecard.css";

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
  onDelete,
}) => {
  const [showCert, setShowCert] = useState(false);

  const handleButtonClick = () => {
    if (showCertificate) {
      setShowCert(true);
    } else {
      window.location.href = actionLink;
    }
  };

  return (
    <>
      <Card className="course-card-wrapper">
        <div className="card-hover-effect">
          <div className="card-img-slide">
            <Card.Img
              src={`/img/${image}`} // Asegúrate que tus imágenes están en public/img
              alt={title}
              className="custom-img"
            />
          </div>
          <div className="card-description-slide">
            <p>{description}</p>
          </div>
        </div>

        <Card.Body className="card-static-info">
          <Card.Title>{title}</Card.Title>
          <Card.Text>Completed: {completed}%</Card.Text>
          <div className="d-grid gap-2">
            <Button
              variant="primary"
              style={{
                backgroundColor: "var(--accent-color)",
                borderColor: "var(--accent-color)",
              }}
              onClick={handleButtonClick}
            >
              {actionText}
            </Button>
            {onDelete && (
              <Button variant="outline-danger" onClick={onDelete}>
                Delete
              </Button>
            )}
          </div>
        </Card.Body>
      </Card>

      {/* Aquí se activa el zoom con react-photo-view */}
      <PhotoProvider>
        {showCert && (
          <PhotoView
            src="/img/Certification.jpeg" // Asegúrate que está dentro de /public/img/
            onClose={() => setShowCert(false)}
          >
            <img
              src="/img/Certification.jpeg"
              alt="Certificate"
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: 0,
                height: 0,
                opacity: 0,
              }}
            />
          </PhotoView>
        )}
      </PhotoProvider>
    </>
  );
};

export default CourseCard;
