import React from "react";
import CourseCard from "../components/CourseCard";
import { Container, Row, Col } from "react-bootstrap";


const Courses = () => {
  const courses = [
    {
      image: "/images/agile.jpg",
      title: "Leadership and Management of Agile Teams",
      completed: 75,
      actionText: "Continue",
      actionLink: "#",
      showCertificate: false,
    },
    {
      image: "/images/data-analysis.jpg",
      title: "Data Analysis for Decision Making",
      completed: 100,
      actionText: "View Certificate",
      actionLink: "#",
      showCertificate: true,
    },
    {
      image: "/images/ai.jpg",
      title: "Fundamentals of Artificial Intelligence",
      completed: 100,
      actionText: "View Certificate",
      actionLink: "#",
      showCertificate: true,
    },
    {
      image: "/images/cybersecurity.jpg",
      title: "Cybersecurity and Data Protection",
      completed: 100,
      actionText: "View Certificate",
      actionLink: "#",
      showCertificate: true,
    },
  ];

  return (
    <Container className="mt-5">
  <h2 className="mb-10">Your courses ðŸ“š</h2>
  <Row className="course-grid-gap">
    {courses.map((course, idx) => (
      <Col md={6} lg={4} className="d-flex mb-4" key={idx}>
      <CourseCard {...course} />
    </Col>
    
    
    ))}
  </Row>
</Container>

  );
};

export default Courses;
