import React from "react";
import { Card, Button} from "react-bootstrap";
import "../styles/Coursecard.css";

const CourseCard = ({ 
    image, 
    title, 
    completed, 
    actionText, 
    actionLink, 
    showCertificate 
  }) => {
    return (
      <Card className="mb-4 shadow-sm h-100 d-flex flex-column">
        <Card.Img variant="top" src={image} style={{ height: '180px', objectFit: 'cover' }} />
        <Card.Body className="d-flex flex-column">
          <Card.Title>{title}</Card.Title>
          <Card.Text>Completed: {completed}%</Card.Text>
  
          <div className="mt-auto">
            {completed < 100 ? (
              <Button className="continue-button" href={actionLink}>{actionText}</Button>
            ) : (
              showCertificate && <Button className="certificate-button" href={actionLink}>{actionText}</Button>
            )}
          </div>
        </Card.Body>
      </Card>
    );
  };
  
  export default CourseCard;
