import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import "../styles/Coursecard.css";

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
}) => {
  const [showModal, setShowModal] = useState(false);

  return (
    <>
      <Card className="course-card">
        {/* Clickable image that opens modal */}
        <button 
          className="course-card-img-btn p-0 border-0 bg-transparent"
          onClick={() => setShowModal(true)}
        >
          <Card.Img
            variant="top"
            src={`/Img/${image}`}
            alt={title}
            className="course-card-img"
          />
        </button>
        
        <Card.Body className="d-flex flex-column">
          <Card.Title className="course-card-title">{title}</Card.Title>
          <Card.Text className="course-card-progress">
            Completed: {completed}%
          </Card.Text>
          <Button
            className={completed === 100 ? "certificate-button" : "continue-button"}
            href={actionLink}
            disabled={completed === 0 && showCertificate}
          >
            {actionText}
          </Button>
        </Card.Body>
      </Card>

      {/* Description Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton className="border-0 pb-0">
          <Modal.Title>{title}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <div className="progress mb-3">
            <div
              className="progress-bar"
              role="progressbar"
              style={{
                width: `${completed}%`,
                backgroundColor: "var(--primary-purple)"
              }}
            >
              {completed}%
            </div>
          </div>
          <p>{description}</p>
        </Modal.Body>
        <Modal.Footer className="border-0">
          <Button 
            variant="outline-dark" 
            onClick={() => setShowModal(false)}
          >
            Close
          </Button>
          <Button 
            className="certificate-button"
            href={actionLink}
          >
            {actionText}
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default CourseCard;
