import React from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Dropdown from "react-bootstrap/Dropdown";

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
  onDelete,
}) => {
  return (
    <Card className="shadow-sm p-2 bg-light rounded h-100 position-relative">
      {onDelete && (
        <Dropdown className="position-absolute top-0 end-0 m-2">
          <Dropdown.Toggle
            variant="light"
            size="sm"
            style={{
              border: "none",
              background: "transparent",
              fontSize: "1.3rem",
              color: "#333",
            }}
          >
            â‹®
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item onClick={onDelete} className="text-danger">
              Remove
            </Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>
      )}

      <Card.Img
        variant="top"
        src={`Img/${image}`}
        onError={(e) => (e.target.src = "/img/default.jpg")} // Imagen fallback
        alt={title}
        style={{ height: "180px", objectFit: "cover" }}
      />
      <Card.Body className="d-flex flex-column justify-content-between">
        <div>
          <Card.Title>{title}</Card.Title>
          {description && <Card.Text>{description}</Card.Text>}
          <Card.Text>Completed: {completed}%</Card.Text>
        </div>
        <div className="d-grid gap-2">
          <Button
            variant={showCertificate && completed === 100 ? "success" : "dark"}
            href={actionLink}
            disabled={completed === 0 && showCertificate}
          >
            {actionText}
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default CourseCard;
