import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import Modal from "react-bootstrap/Modal";
import "../styles/Coursecard.css"; // Asegúrate de que la ruta sea correcta

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
}) => {
  const [showModal, setShowModal] = useState(false);

  const renderPopover = (
    <Popover id={`popover-${title}`} className="custom-popover">
      <Popover.Body className="popover-body">
        <p>{description}</p>
      </Popover.Body>
    </Popover>
  );

  const handleButtonClick = () => {
    if (showCertificate) {
      setShowModal(true);
    } else {
      window.location.href = actionLink;
    }
  };

  return (
    <>
      <OverlayTrigger
        placement="top"
        delay={{ show: 150, hide: 300 }}
        overlay={renderPopover}
        trigger={["hover", "focus"]}
      >
        <Card className="shadow-sm p-2 bg-light rounded h-100 position-relative course-card-hover">
          <Card.Img
            variant="top"
            src={`/Img/${image}`}
            alt={title}
            style={{ height: "180px", objectFit: "cover" }}
          />
          <Card.Body className="d-flex flex-column justify-content-between text-center">
            <Card.Title>{title}</Card.Title>
            <Card.Text>Completed: {completed}%</Card.Text>
            <div className="d-grid gap-2">
              <Button
                variant="primary"
                style={{ backgroundColor: "var(--accent-color)", borderColor: "var(--accent-color)" }}
                onClick={handleButtonClick}
              >
                {actionText}
              </Button>
            </div>
          </Card.Body>
        </Card>
      </OverlayTrigger>

      {showCertificate && (
        <Modal show={showModal} onHide={() => setShowModal(false)} centered>
          <Modal.Header closeButton>
            <Modal.Title>{title} - Certificate</Modal.Title>
          </Modal.Header>
          <Modal.Body className="text-center">
            <img
              src="Img/certification.jpeg"  // Cambia si el path es distinto
              alt="Certificate"
              className="img-fluid rounded shadow-sm"
              style={{ maxHeight: "80vh" }}
            />
          </Modal.Body>
          <Modal.Footer>
            <Button
              variant="secondary"
              onClick={() => setShowModal(false)}
              style={{ backgroundColor: "var(--accent-color)", borderColor: "var(--accent-color)" }}
            >
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </>
  );
};

export default CourseCard;
