import React from "react";
import PropTypes from "prop-types";
import { Card, Button } from "react-bootstrap";

const CertificationCard = ({ name, description, expiration, onView }) => {
  return (
    <Card className="h-100 shadow-sm border-0">
      <Card.Body>
        <Card.Title className="text-primary fw-bold">{name}</Card.Title>
        <Card.Text className="text-muted">{description}</Card.Text>
        <p>
          <strong>Expira:</strong>{" "}
          <span className="text-danger">{expiration}</span>
        </p>
        <Button variant="outline-primary" onClick={onView}>
          Ver certificación
        </Button>
      </Card.Body>
    </Card>
  );
};

CertificationCard.propTypes = {
  name: PropTypes.string.isRequired,
  description: PropTypes.string.isRequired,
  expiration: PropTypes.string.isRequired,
  onView: PropTypes.func,
};

CertificationCard.defaultProps = {
  onView: () => alert("Función para ver la certificación aún no implementada"),
};

export default CertificationCard;
