import React from "react";
import PropTypes from "prop-types";
import { Card, ListGroup } from "react-bootstrap";
import "../styles/Notification.css";

const Notifications = ({ notifications }) => (
  <div className="modern-notification-container">
    <Card className="modern-notification-card">
      <Card.Header className="modern-notification-header">
        <div className="header-content">
          <div className="bell-icon-container">
            <svg className="bell-icon" viewBox="0 0 24 24">
              <path d="M12 22c1.1 0 2-.9 2-2h-4c0 1.1.9 2 2 2zm6-6v-5c0-3.07-1.63-5.64-4.5-6.32V4c0-.83-.67-1.5-1.5-1.5s-1.5.67-1.5 1.5v.68C7.64 5.36 6 7.92 6 11v5l-2 2v1h16v-1l-2-2z"/>
            </svg>
          </div>
          <h3 className="notification-title">Notifications</h3>
        </div>
      </Card.Header>

      <Card.Body className="p-0">
        {notifications.length > 0 ? (
          <ListGroup variant="flush" className="modern-notification-list">
            {notifications.map((msg, i) => (
              <ListGroup.Item key={i} className="modern-notification-item">
                <div className="notification-indicator"></div>
                <div className="notification-content">
                  <p className="notification-message">{msg}</p>
                  <span className="notification-time">Just now</span>
                </div>
              </ListGroup.Item>
            ))}
          </ListGroup>
        ) : (
          <div className="modern-notification-empty">
            <svg className="check-icon" viewBox="0 0 24 24">
              <path d="M9 16.17L4.83 12l-1.42 1.41L9 19 21 7l-1.41-1.41L9 16.17z"/>
            </svg>
            <p>No new notifications</p>
          </div>
        )}
      </Card.Body>
    </Card>
  </div>
);

Notifications.propTypes = {
  notifications: PropTypes.arrayOf(PropTypes.string).isRequired,
};

export default Notifications;
