import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Header from "../components/Header";
import CertificationCard from "../components/CertificationCard";
import "../styles/HomePage.css";
import { useNotifications } from "../helpers/NotificationContext"; // ✅ Cambio aquí



function HomePage() {
  const [certs, setCerts] = useState([]);
  const { notifications, addNotifications } = useNotifications();

  // ✅ usamos useCallback para evitar el warning de eslint
  const fetchCertifications = useCallback(async () => {
    try {
      const token = localStorage.getItem("authToken");

      const response = await fetch("https://pathfinder-back-hnoj.onrender.com/employees/certifications", {
        headers: {
          "Content-Type": "application/json",
          token: token,
        },
      });

      if (!response.ok) {
        throw new Error("Error en la solicitud");
      }

      const data = await response.json();
      const certsData = data.certificationsOfEmployee.map((cert) => ({
        id: cert.id,
        name: cert.name,
        description: cert.description,
        expiration: cert.Certinfo.expiration,
      }));

      setCerts(certsData);

      const now = new Date();
      const expiringSoon = certsData.filter((cert) => {
        const expirationDate = new Date(cert.expiration);
        const diffDays = (expirationDate - now) / (1000 * 60 * 60 * 24);
        return diffDays <= 7;
      });

      if (expiringSoon.length > 0) {
        const newNotifs = expiringSoon.map(
          (c) => `The certification "${c.name}" is expiring soon`
        );
        addNotifications(newNotifs);
      }
    } catch (error) {
      console.error("Error obteniendo certificaciones:", error);
      addNotifications(["Hubo un error al cargar las certificaciones"]);
    }
  }, [addNotifications]); // ✅ dependencias para useCallback

  // ✅ se usa fetchCertifications como dependencia
  useEffect(() => {
    fetchCertifications();
  }, [fetchCertifications]);

  return (
    <div className="homepage-container">
      <Navbar />
      <div className="homepage-content">
        <Header />
        <h2 className="certifications-title">My Certifications</h2>
        <div className="certifications-list">
          {certs.map((cert) => (
            <CertCard
              key={cert.id}
              name={cert.name}
              description={cert.description}
              expiration={cert.expiration}
            />
          ))}
        </div>
      </div>
    </div>
  );
}

export default HomePage;
