import React from "react";
import PropTypes from "prop-types";
import { Card, Badge, ListGroup } from "react-bootstrap";
import "../styles/Notification.css";

const Notifications = ({ notifications }) => (
  <Card className="shadow-sm border-0">
    <Card.Header className="d-flex justify-content-between align-items-center bg-light">
      <div className="d-flex align-items-center">
        <i className="bi bi-bell-fill me-2 fs-5 text-primary"></i>
        <h5 className="mb-0">Notificaciones</h5>
      </div>
      <Badge bg="primary" pill>
        {notifications.length}
      </Badge>
    </Card.Header>

    <Card.Body className="p-0">
      {notifications.length > 0 ? (
        <ListGroup variant="flush">
          {notifications.map((msg, i) => (
            <ListGroup.Item key={i} className="d-flex align-items-start px-4 py-3">
              <i className="bi bi-dot fs-4 text-primary me-2"></i>
              <span className="text-dark">{msg}</span>
            </ListGroup.Item>
          ))}
        </ListGroup>
      ) : (
        <div className="text-center py-4 text-secondary">
          No hay notificaciones nuevas
        </div>
      )}
    </Card.Body>
  </Card>
);

Notifications.propTypes = {
  notifications: PropTypes.arrayOf(PropTypes.string).isRequired,
};

export default Notifications;
