import React from "react";
import PropTypes from "prop-types";
import { Card, ProgressBar, Badge, ListGroup} from "react-bootstrap";
import { Folder2Open, Laptop } from "react-bootstrap-icons";

const Projects = ({ projects }) => (
  <Card className="shadow-sm border-0">
    <Card.Header className="bg-white border-bottom-0 d-flex align-items-center">
      <Folder2Open className="me-2 text-primary" size={20} />
      <h5 className="mb-0 fw-bold">Projects</h5>
      <Badge pill bg="light" text="dark" className="ms-auto">
        {projects.length} active
      </Badge>
    </Card.Header>
    <Card.Body className="pt-0">
      <ListGroup variant="flush">
        {projects.map((project, i) => (
          <ListGroup.Item key={i} className="border-0 py-3 px-0">
            <div className="d-flex justify-content-between mb-2">
              <h6 className="mb-0 fw-semibold">{project.name}</h6>
              <small className="text-muted">
                <Laptop className="me-1" size={12} />
                {project.platform}
              </small>
            </div>
            <div className="d-flex align-items-center">
              <ProgressBar 
                now={project.percentage} 
                className="flex-grow-1 me-3" 
                variant={getProgressVariant(project.percentage)}
                style={{ height: '6px' }}
              />
              <small className="fw-bold" style={{ minWidth: '40px' }}>
                {project.percentage}%
              </small>
            </div>
          </ListGroup.Item>
        ))}
      </ListGroup>
      {projects.length === 0 && (
        <div className="text-center py-4 text-muted">
          No active projects
        </div>
      )}
    </Card.Body>
  </Card>
);
const getProgressVariant = (percentage) => {
  if (percentage >= 90) return 'success';
  if (percentage >= 70) return 'info';
  if (percentage >= 50) return 'primary';
  if (percentage >= 30) return 'warning';
  return 'danger';
};

Projects.propTypes = {
  projects: PropTypes.arrayOf(
    PropTypes.shape({
      name: PropTypes.string.isRequired,
      platform: PropTypes.string.isRequired,
      percentage: PropTypes.number.isRequired,
    })
  ).isRequired,
};

export default Projects;
