import React, { useEffect, useState } from "react";
import CourseCard from "./CourseCard";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Spinner from "react-bootstrap/Spinner";
import Alert from "react-bootstrap/Alert";

const Courses = () => {
  const [courses, setCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchCourses = async () => {
    try {
      const response = await fetch("http://localhost:3000/courses"); // Ajusta el endpoint si es necesario
      if (!response.ok) throw new Error("Error fetching courses");
      const data = await response.json();
      setCourses(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCourses();
  }, []);

  const handleDelete = (title) => {
    const updated = courses.filter((course) => course.name !== title);
    setCourses(updated);
  };

  if (loading) {
    return (
      <Container className="text-center my-5">
        <Spinner animation="border" />
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="my-5">
        <Alert variant="danger">Error: {error}</Alert>
      </Container>
    );
  }

  return (
    <Container className="my-4">
      <h2 className="mb-4">My Courses</h2>
      <Row xs={1} sm={2} md={3} lg={4} className="g-4">
        {courses.map((course, index) => (
          <Col key={index}>
            <CourseCard
              image={course.imgUrl}
              title={course.name}
              description={course.description}
              completed={course.Courseinfo.status}
              actionText={
                course.Courseinfo.status === 100
                  ? "View Certificate"
                  : "Continue"
              }
              actionLink="#"
              showCertificate={course.Courseinfo.status === 100}
              onDelete={() => handleDelete(course.name)}
            />
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default Courses;
