import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "react-bootstrap";
import Notifications from "../components/Notifications";
import Projects from "../components/Projects";
import CertificationCard from "../components/CertificationCard";
import "../styles/HomePage.css";
import dayjs from "dayjs";

const HomePage = () => {
  const [notifications, setNotifications] = useState([]);
  const [projects, setProjects] = useState([]);
  const [certs, setCerts] = useState([]);
  const name = localStorage.getItem("userName") || "Usuario";

  useEffect(() => {
    // Proyectos locales
    setProjects([
      {
        name: "Plataforma de Banca Digital",
        platform: "BBanco",
        percentage: 80,
      },
      {
        name: "Portal de Gestión de Créditos",
        platform: "FinanZapp",
        percentage: 50,
      },
      {
        name: "Plataforma de Inversiones",
        platform: "InvestEasy",
        percentage: 90,
      },
    ]);

    const fetchCertifications = async () => {
      try {
        const res = await fetch(
          "https://pathfinder-back-hnoj.onrender.com/employees/certifications",
          {
            headers: {
              token: localStorage.getItem("token"),
            },
          }
        );
        const data = await res.json();

        if (data.certificationsOfEmployee) {
          const newNotifs = [];
          const certData = data.certificationsOfEmployee.map((cert) => {
            const expiration = dayjs(cert.Certinfo.expiration);
            const today = dayjs();
            const diffDays = expiration.diff(today, "day");

            if (diffDays <= 7) {
              newNotifs.push(
                `⚠️ La certificación "${cert.name}" vence en ${diffDays} días (${expiration.format("DD/MM/YYYY")})`
              );
            }

            return {
              name: cert.name,
              description: cert.description,
              expiration: expiration.format("DD/MM/YYYY"),
            };
          });

          setCerts(certData);
          setNotifications((prev) => [...prev, ...newNotifs]);
        }
      } catch (err) {
        console.error("Error al obtener certificaciones", err);
      }
    };

    fetchCertifications();
  }, []);

  return (
    <>
      <section className="hero-section">
        <div className="container hero-text">
          <h1>Bienvenido, {name}</h1>
          <p>Consulta tus notificaciones y proyectos activos</p>
        </div>
      </section>

      <Container className="homepage-container fade-in">
        <Row>
          <Col md={6}>
            <Notifications notifications={notifications} />
          </Col>
          <Col md={6}>
            <Projects projects={projects} />
          </Col>
        </Row>

        <Row className="mt-4">
        <h4 className="mb-3">Certificaciones</h4>
        {certs.map((cert, i) => (
          <Col key={i} md={6} className="mb-3">
            <CertificationCard
              name={cert.name}
              description={cert.description}
              expiration={cert.expiration}
              onView={() => {
                console.log(`Ver detalles de: ${cert.name}`);
                // Aquí puedes abrir un modal, navegar, etc.
              }}
            />
          </Col>
        ))}
      </Row>
      </Container>
    </>
  );
};

export default HomePage;
