import React from "react";
import { Card, Button, OverlayTrigger, Popover, Modal } from "react-bootstrap";
import "../styles/Coursecard.css"; // Asegúrate de que la ruta sea correcta

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  onActionClick,
  showCertificate,
  onDelete
}) => {
  const [showModal, setShowModal] = React.useState(false);

  const renderPopover = (
    <Popover id={`popover-${title}`} className="custom-popover">
      <Popover.Body className="popover-body">
        {description}
      </Popover.Body>
    </Popover>
  );

  const handleActionClick = () => {
    if (showCertificate) {
      setShowModal(true);
    } else if (onActionClick) {
      onActionClick();
    }
  };

  return (
    <>
      <OverlayTrigger
        placement="top"
        delay={{ show: 150, hide: 300 }}
        overlay={renderPopover}
        trigger={["hover", "focus"]}
      >
        <Card className="course-card">
          <Card.Img
            variant="top"
            src={`/Img/${image}`}
            alt={title}
            className="course-card-img"
          />
          
          <Card.Body className="course-card-body">
            <Card.Title className="course-card-title">{title}</Card.Title>
            
            <div className="progress-container">
              <div 
                className="progress-bar"
                style={{ width: `${completed}%` }} 
              />
            </div>
            
            <span className="progress-text">
              {completed}% completed
            </span>
            
            <div style={{ width: "100%" }}>
              <Button
                className={`course-card-button ${showCertificate ? 'outline' : ''}`}
                onClick={handleActionClick}
                disabled={completed < 100 && showCertificate}
              >
                {actionText}
              </Button>
              
              {showCertificate && (
                <Button
                  variant="outline-danger"
                  className="delete-button"
                  onClick={onDelete}
                >
                  Delete
                </Button>
              )}
            </div>
          </Card.Body>
        </Card>
      </OverlayTrigger>

      {/* Modal para el certificado */}
      <Modal
        show={showModal}
        onHide={() => setShowModal(false)}
        centered
        backdropClassName="modal-blur"
        dialogClassName="modal-content-custom"
      >
        <Modal.Header closeButton className="modal-header-custom">
          <Modal.Title>{title} - Certificate</Modal.Title>
        </Modal.Header>
        <Modal.Body className="modal-body-custom">
          <img
            src="Img/certification.jpeg" 
            alt="Certificate"
            className="modal-img"
          />
        </Modal.Body>
        <Modal.Footer className="modal-footer-custom">
          <Button 
            variant="primary" 
            onClick={() => setShowModal(false)}
            style={{ backgroundColor: "var(--accent-color)", borderColor: "var(--accent-color)" }}
          >
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default CourseCard;
