import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../helpers/UserContext";
import "../styles/Profile.css";

const Profile = () => {
  const [projects, setProjects] = useState([]);
  const [completedCourses, setCompletedCourses] = useState([]);
  const { user, setUser } = useContext(UserContext);

  const token = localStorage.getItem("token");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await fetch("https://pathfinder-back-hnoj.onrender.com/employees/profile", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            token,
          },
        });
        const data = await response.json();
        setUser(data.employee);
      } catch (error) {
        console.error("Error fetching user profile:", error);
      }
    };

    const fetchInactiveProjects = async () => {
      try {
        const response = await fetch("https://pathfinder-back-hnoj.onrender.com/employees/inactive-projects", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            token,
          },
        });
        const data = await response.json();
        setProjects(data.inactiveProjects || []);
      } catch (error) {
        console.error("Error fetching inactive projects:", error);
      }
    };

    const getCompletedCoursesFromStorage = () => {
      const stored = localStorage.getItem("completedCourses");
      if (stored) {
        try {
          const parsed = JSON.parse(stored);
          setCompletedCourses(parsed);
        } catch (e) {
          console.error("Error parsing completedCourses from localStorage:", e);
        }
      }
    };

    fetchProfileData();
    fetchInactiveProjects();
    getCompletedCoursesFromStorage();
  }, [token, setUser]);

  return (
    <div className="profile">
      <div className="profile-info">
        <img src="/profile.webp" alt="Profile" />
        <h2>{user.name}</h2>
        <p>{user.email}</p>
        <p>Puesto: {user.role}</p>
      </div>

      <div className="profile-section">
        <h3>Proyectos finalizados:</h3>
        <ul>
          {projects.map((project) => (
            <li key={project.id}>{project.name}</li>
          ))}
        </ul>
      </div>

      <div className="profile-section">
        <h3>Cursos completados:</h3>
        {completedCourses.length > 0 ? (
          <ul>
            {completedCourses.map((course, index) => (
              <li key={index}>{course}</li>
            ))}
          </ul>
        ) : (
          <p>No hay cursos completados todav√≠a.</p>
        )}
      </div>
    </div>
  );
};

export default Profile;
