import React from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
}) => {
  // Popover que se mostrará al pasar el mouse
  const renderPopover = (
    <Popover id={`popover-${title}`}>
      <Popover.Header as="h3">Description</Popover.Header>
      <Popover.Body>{description}</Popover.Body>
    </Popover>
  );

  return (
    <OverlayTrigger
      placement="top"
      delay={{ show: 150, hide: 300 }}
      overlay={renderPopover}
    >
      <Card className="shadow-sm p-2 bg-light rounded h-100 position-relative course-card-hover">
        <Card.Img
          variant="top"
          src={`/Img/${image}`}
          alt={title}
          style={{ height: "180px", objectFit: "cover" }}
        />
        <Card.Body className="d-flex flex-column justify-content-between">
          <Card.Title>{title}</Card.Title>
          <Card.Text>Completed: {completed}%</Card.Text>
          <div className="d-grid gap-2">
            <Button
              variant="dark"
              href={actionLink}
              disabled={completed === 0 && showCertificate}
            >
              {actionText}
            </Button>
          </div>
        </Card.Body>
      </Card>
    </OverlayTrigger>
  );
};

export default CourseCard;
