import React from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";
import "./CourseCard.css"; // Create this file for custom styles

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
}) => {
  // Custom styled popover
  const renderPopover = (
    <Popover id={`popover-${title}`} className="custom-popover">
      <Popover.Header as="h3" className="popover-header">
        {title}
        <div className="progress mt-2">
          <div
            className="progress-bar bg-success"
            role="progressbar"
            style={{ width: `${completed}%` }}
            aria-valuenow={completed}
            aria-valuemin="0"
            aria-valuemax="100"
          >
            {completed}%
          </div>
        </div>
      </Popover.Header>
      <Popover.Body className="popover-body">
        <p>{description}</p>
        <Button
          variant="outline-dark"
          size="sm"
          href={actionLink}
          className="mt-2"
          disabled={completed === 0 && showCertificate}
        >
          {actionText}
        </Button>
      </Popover.Body>
    </Popover>
  );

  return (
    <OverlayTrigger
      placement="top"
      delay={{ show: 150, hide: 300 }}
      overlay={renderPopover}
      trigger={["hover", "focus"]}
    >
      <Card className="shadow-sm p-2 bg-light rounded h-100 position-relative course-card-hover">
        <Card.Img
          variant="top"
          src={`/Img/${image}`}
          alt={title}
          style={{ height: "180px", objectFit: "cover" }}
        />
        <Card.Body className="d-flex flex-column justify-content-between">
          <Card.Title>{title}</Card.Title>
          <Card.Text>Completed: {completed}%</Card.Text>
          <div className="d-grid gap-2">
            <Button
              variant="dark"
              href={actionLink}
              disabled={completed === 0 && showCertificate}
            >
              {actionText}
            </Button>
          </div>
        </Card.Body>
      </Card>
    </OverlayTrigger>
  );
};

export default CourseCard;
