import React, { useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import "../styles/CourseCard.css"; // Para estilos personalizados

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
}) => {
  const [showModal, setShowModal] = useState(false);

  const handleOpen = () => setShowModal(true);
  const handleClose = () => setShowModal(false);

  return (
    <>
      <Card
        className="shadow-sm p-2 bg-light rounded h-100 position-relative course-card-hover"
        onMouseEnter={handleOpen}
        onMouseLeave={handleClose}
      >
        <Card.Img
          variant="top"
          src={`/Img/${image}`}
          alt={title}
          style={{ height: "180px", objectFit: "cover" }}
        />
        <Card.Body className="d-flex flex-column justify-content-between">
          <Card.Title>{title}</Card.Title>
          <Card.Text>Completed: {completed}%</Card.Text>
          <div className="d-grid gap-2">
            <Button
              variant="dark"
              href={actionLink}
              disabled={completed === 0 && showCertificate}
            >
              {actionText}
            </Button>
          </div>
        </Card.Body>
      </Card>

      <Modal
        show={showModal}
        onHide={handleClose}
        centered
        backdropClassName="custom-backdrop"
        contentClassName="custom-modal-content"
      >
        <Modal.Header closeButton>
          <Modal.Title>Description</Modal.Title>
        </Modal.Header>
        <Modal.Body>{description}</Modal.Body>
      </Modal>
    </>
  );
};

export default CourseCard;
