import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Button, Modal } from 'react-bootstrap';
import Notifications from './Notifications'; // Asegúrate de que la ruta sea correcta

const Header = ({ title, subtitle, notifications }) => {
  const [showNotifications, setShowNotifications] = useState(false);
  
  // Contador de notificaciones no leídas (puedes personalizar esta lógica)
  const notificationCount = notifications.length;

  const handleBellClick = () => {
    setShowNotifications(true);
  };

  const handleCloseNotifications = () => {
    setShowNotifications(false);
  };

  return (
    <>
      <section className="hero-section">
        <div className="container hero-text">
          <div className="d-flex justify-content-between align-items-start">
            <div>
              <h1>{title}</h1>
              {subtitle && <p>{subtitle}</p>}
            </div>
            <div className="notification-icon-container">
              <Button 
                variant="link" 
                className="notification-bell p-0 position-relative"
                onClick={handleBellClick}
                aria-label="Notifications"
              >
                <i className="bi bi-bell-fill fs-3 text-purple"></i>
                {notificationCount > 0 && (
                  <span className="position-absolute top-0 start-100 translate-middle badge rounded-pill bg-danger">
                    {notificationCount}
                    <span className="visually-hidden">unread notifications</span>
                  </span>
                )}
              </Button>
            </div>
          </div>
        </div>
      </section>

      {/* Modal de Notificaciones */}
      <Modal show={showNotifications} onHide={handleCloseNotifications} centered>
        <Modal.Header closeButton>
          <Modal.Title>Notificaciones</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Notifications notifications={notifications} />
        </Modal.Body>
      </Modal>
    </>
  );
};

Header.propTypes = {
  title: PropTypes.string.isRequired,
  subtitle: PropTypes.string,
  notifications: PropTypes.arrayOf(PropTypes.string).isRequired
};

Header.defaultProps = {
  subtitle: '',
};

export default Header;
