import React, { useEffect, useState } from "react";
import "./Profile.css";
import profileImage from "../images/profile.webp";

const Profile = () => {
  const [user, setUser] = useState({});
  const [projects, setProjects] = useState([]);
  const [completedCourses, setCompletedCourses] = useState([]);

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchProfileData = async () => {
      try {
        const response = await fetch("https://pathfinder-back-hnoj.onrender.com/employees/profile", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            token,
          },
        });
        const data = await response.json();
        setUser(data.employee);
      } catch (error) {
        console.error("Error fetching user profile:", error);
      }
    };

    const fetchInactiveProjects = async () => {
      try {
        const response = await fetch("https://pathfinder-back-hnoj.onrender.com/employees/inactive-projects", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            token,
          },
        });
        const data = await response.json();
        setProjects(data.inactiveProjects || []);
      } catch (error) {
        console.error("Error fetching inactive projects:", error);
      }
    };

    const fetchCompletedCourses = async () => {
      try {
        const response = await fetch("https://pathfinder-back-hnoj.onrender.com/employees/courses", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
            token,
          },
        });

        const data = await response.json();
        if (!data.error) {
          const fetchedCourses = data.coursesOfEmployee || [];
          const completed = fetchedCourses
            .filter((course) => Number(course.Courseinfo.status) === 100)
            .map((course) => course.name);
          localStorage.setItem("completedCourses", JSON.stringify(completed));
          setCompletedCourses(completed);
        }
      } catch (error) {
        console.error("Error fetching completed courses:", error);
      }
    };

    fetchProfileData();
    fetchInactiveProjects();
    fetchCompletedCourses();
  }, [token]);

  return (
    <div className="profile">
      <div className="profile-info">
        <img src={profileImage} alt="Profile" />
        <h2>{user.name}</h2>
        <p>{user.email}</p>
        <p>Puesto: {user.role}</p>
      </div>

      <div className="profile-section">
        <h3>Proyectos finalizados:</h3>
        <ul>
          {projects.map((project) => (
            <li key={project.id}>{project.name}</li>
          ))}
        </ul>
      </div>

      <div className="profile-section">
        <h3>Cursos completados:</h3>
        <ul>
          {completedCourses.map((course, index) => (
            <li key={index}>{course}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Profile;
