import React from "react";
import { Card, Button, OverlayTrigger, Popover } from "react-bootstrap";
import "../styles/Coursecard.css"; // Assuming you have a CSS file for styles

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  onActionClick,
  showCertificate,
  onDelete
}) => {
  const renderPopover = (
    <Popover id={`popover-${title}`} className="custom-popover">
      <Popover.Body className="popover-body">
        {description}
      </Popover.Body>
    </Popover>
  );

  return (
    <OverlayTrigger
      placement="top"
      delay={{ show: 150, hide: 300 }}
      overlay={renderPopover}
      trigger={["hover", "focus"]}
    >
      <Card className="course-card">
        <Card.Img
          variant="top"
          src={`/Img/${image}`}
          alt={title}
          className="course-card-img"
        />
        
        <Card.Body className="course-card-body">
          <Card.Title className="course-card-title">{title}</Card.Title>
          
          <div className="progress-container">
            <div 
              className="progress-bar"
              style={{ width: `${completed}%` }} 
            />
          </div>
          
          <span className="progress-text">
            {completed}% completed
          </span>
          
          <div className="course-card-footer">
            <Button
              variant={showCertificate ? "outline-primary" : "primary"}
              className="course-card-button"
              href={onActionClick ? undefined : actionLink}
              onClick={onActionClick}
              disabled={completed < 100 && showCertificate}
            >
              {actionText}
            </Button>
            
            {showCertificate && (
              <Button
                variant="outline-danger"
                className="delete-button"
                onClick={onDelete}
              >
                Delete
              </Button>
            )}
          </div>
        </Card.Body>
      </Card>
    </OverlayTrigger>
  );
};

export default CourseCard;
