import React from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Popover from "react-bootstrap/Popover";

const CourseCard = ({
  image,
  title,
  description,
  completed,
  actionText,
  actionLink,
  showCertificate,
}) => {
  // Custom styled popover
  const renderPopover = (
    <Popover id={`popover-${title}`} className="custom-popover">
      <Popover.Body className="p-3">
        <h5 className="mb-2">{title}</h5>
        <p className="text-muted mb-3">{description}</p>
        <div className="d-flex justify-content-between align-items-center">
          <div className="progress flex-grow-1 me-3" style={{ height: "6px" }}>
            <div
              className="progress-bar"
              role="progressbar"
              style={{ 
                width: `${completed}%`,
                backgroundColor: "var(--primary-purple)"
              }}
            />
          </div>
          <small className="text-nowrap">{completed}% completed</small>
        </div>
      </Popover.Body>
    </Popover>
  );

  return (
    <OverlayTrigger
      placement="top"
      delay={{ show: 150, hide: 300 }}
      overlay={renderPopover}
      trigger={["hover", "focus"]}
    >
      <Card className="course-card">
        <Card.Img
          variant="top"
          src={`/Img/${image}`}
          alt={title}
          className="course-card-img"
        />
        <Card.Body className="d-flex flex-column">
          <Card.Title className="course-card-title">{title}</Card.Title>
          <div className="d-flex align-items-center mb-2">
            <div className="progress flex-grow-1 me-2" style={{ height: "4px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                style={{ 
                  width: `${completed}%`,
                  backgroundColor: "var(--primary-purple)"
                }}
              />
            </div>
            <small>{completed}%</small>
          </div>
          <Button
            className={completed === 100 ? "certificate-button" : "continue-button"}
            href={actionLink}
            disabled={completed === 0 && showCertificate}
          >
            {actionText}
          </Button>
        </Card.Body>
      </Card>
    </OverlayTrigger>
  );
};

export default CourseCard;
